	private Integer lessonId;
	private String question;
	private String answer;
	
	
	public Edit_FillInTheBlank_dto() {
		super();
		// TODO Auto-generated constructor stub
	}


	public Integer getId() {
		return id;
	}


	public void setId(Integer id) {
		this.id = id;
	}


